    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //Botão Gravar:
        try {                                                           
            if(campoNome.getText().equals("")|| campoProduto.getText().equals("") || campoProduto.getText().equals("")) jButton1ActionPerformed(dispatchEvent(ERROR));            
            p.push(new Compra(campoNome.getText(), campoProduto.getText(),Double.parseDouble(campoPreco.getText())));
            textArea1.setText(p.toString());
            campoNome.setText("");
            campoProduto.setText("");
            campoPreco.setText("");
            campoNome.requestFocus();
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Preencha todos os Campos !", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //Botão Cancelar:
        try {
            if(p.isEmpty() == true) jButton2ActionPerformed(dispatchEvent(ERROR));
            int resp = JOptionPane.showConfirmDialog(null, "Deseja cancelar a última compra ?\n\n" + p.top(), "Aviso", JOptionPane.YES_NO_OPTION);                    
            if(resp == 0){                
               p.pop();
               textArea1.setText(p.toString());
            }                                                               
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Não há compras para remover !", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        //Botão Transferir:
        try {
            if(p.isEmpty() == true) jButton2ActionPerformed(dispatchEvent(ERROR));
            int resp = JOptionPane.showConfirmDialog(null, "Deseja transferir os dados da pilha para a fila ?", "Aviso", JOptionPane.YES_NO_OPTION);
            if(resp != 0) jButton2ActionPerformed(dispatchEvent(ERROR));
            String aux = "";
            while(!p.isEmpty()){
                aux += p.pop() + "\n";            
            }
            for(int i = aux.split("\n").length - 1; i >= 0; i--){
                f.enqueue(aux.split("\n")[i]);
            }
            aux = "";        
            textArea1.setText(f.toString());            
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Não há dados para transferir!", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }        
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        //Botão Atender
        try {
            if(f.isEmpty() == true) jButton2ActionPerformed(dispatchEvent(ERROR));
            JOptionPane.showMessageDialog(null, "Cliente Atendido:\n\n" + f.dequeue(), "Aviso", JOptionPane.INFORMATION_MESSAGE);
            textArea1.setText(f.toString());
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null, "Não há clientes para atender !", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_jButton4ActionPerformed

