    private void campoPrecoKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_campoPrecoKeyTyped
        String num = "0987654321.,";                
        if(!num.contains(evt.getKeyChar()+"")){
            evt.consume();
            JOptionPane.showMessageDialog(null, "Somente números !", "Aviso", JOptionPane.INFORMATION_MESSAGE);
        }
    }//GEN-LAST:event_campoPrecoKeyTyped

